import React, { useState, useEffect } from "react";
import { Container } from "@mui/material";
import { useLocation, useNavigate } from "react-router-dom";
import api from "../utils/axios";
import Table from "../components/Table";
import CustomButton from "../components/Button";
import PageHeader from "../components/PageHeader";
import SearchField from "../components/SearchField";
import styles from "./styles/OrganisationsPage.module.css";

const OrganisationsPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const tenantId = location.state?.tenantId || 1;
  const [organisations, setOrganisations] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    const fetchOrganisations = async () => {
      try {
        setLoading(true);
        const response = await api.get("/api/organisations", {
          params: { tenantId }
        });
        console.log('API response:', response.data);
        setOrganisations(response.data || []);
        setError(null);
      } catch (error) {
        console.error("Error fetching organisations:", error);
        setError("Failed to fetch organisations");
      } finally {
        setLoading(false);
      }
    };

    fetchOrganisations();
  }, [tenantId]);

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  // Define columns for the table
  const columns = [
    { field: 'id', headerName: 'ID' },
    { field: 'name', headerName: 'Name' },
    { field: 'organizationOwner', headerName: 'Owner' },
    { 
      field: 'tenantId', 
      headerName: 'Tenant',
      valueGetter: (row) => `Tenant ${row.tenantId}` // Temporary display until we have tenant names
    }
  ];

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <Container className={styles.container}>
      <PageHeader 
        title="Organisations"
        buttonText="Create New Organisation"
        onButtonClick={() => navigate('/organisations/create')}
      >
        <SearchField
          value={searchTerm}
          onChange={handleSearch}
          placeholder="Search organisations..."
          className={styles.searchField}
        />
      </PageHeader>
      
      {organisations.length === 0 ? (
        <div className={styles.emptyState}>
          <p>No organisations created for tenant ID: {tenantId}</p>
          <CustomButton 
            variant="primary"
            size="medium"
            onClick={() => navigate('/organisations/create')}
          >
            Create New Organisation
          </CustomButton>
        </div>
      ) : (
        <Table 
          data={organisations} 
          columns={columns} 
        />
      )}
    </Container>
  );
};

export default OrganisationsPage;
