import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import api from "../utils/axios";
import Table from "../components/Table";
import CustomButton from "../components/Button";
import styles from "./styles/OrganisationsPage.module.css";

const OrganisationsPage = () => {
  const location = useLocation();
  const tenantId = location.state?.tenantId || 1;
  const [organisations, setOrganisations] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchOrganisations = async () => {
      try {
        setLoading(true);
        const response = await api.get("/api/organisations", {
          params: { tenantId }
        });
        setOrganisations(response.data);
        setError(null);
      } catch (error) {
        console.error("Error fetching organisations:", error);
        setError("Failed to fetch organisations");
      } finally {
        setLoading(false);
      }
    };

    fetchOrganisations();
  }, [tenantId]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className={styles.container}>
      <h1>Organisations</h1>
      {organisations.length === 0 ? (
        <div>
          <p>No organisations created for tenant ID: {tenantId}</p>
          <CustomButton 
            variant="primary"
            size="medium"
            onClick={() => console.log("Redirect to create organisation form")}
          >
            Create New Organisation
          </CustomButton>
        </div>
      ) : (
        <>
          <CustomButton 
            variant="secondary"
            size="medium"
            onClick={() => console.log("Redirect to create organisation form")}
          >
            Create New Organisation
          </CustomButton>
          <Table data={organisations} />
        </>
      )}
    </div>
  );
};

export default OrganisationsPage;
