import React, { useState, useEffect } from "react";
import { Container, TextField, InputAdornment } from "@mui/material";
import SearchIcon from '@mui/icons-material/Search';
import { useLocation, useNavigate } from "react-router-dom";
import api from "../utils/axios";
import Table from "../components/Table";
import CustomButton from "../components/Button";
import PageHeader from "../components/PageHeader";
import styles from "./styles/OrganisationsPage.module.css";

const OrganisationsPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const tenantId = location.state?.tenantId || 1;
  const [organisations, setOrganisations] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    const fetchOrganisations = async () => {
      try {
        setLoading(true);
        const response = await api.get("/api/organisations", {
          params: { tenantId }
        });
        setOrganisations(response.data);
        setError(null);
      } catch (error) {
        console.error("Error fetching organisations:", error);
        setError("Failed to fetch organisations");
      } finally {
        setLoading(false);
      }
    };

    fetchOrganisations();
  }, [tenantId]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <Container className={styles.container}>
      <PageHeader 
        title="Organisations"
        buttonText="Create New Organisation"
        onButtonClick={() => navigate('/organisations/create')}
      >
        <TextField
          placeholder="Search organisations..."
          size="small"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">
                <SearchIcon />
              </InputAdornment>
            ),
          }}
          className={styles.searchField}
        />
      </PageHeader>
      
      {organisations.length === 0 ? (
        <div className={styles.emptyState}>
          <p>No organisations created for tenant ID: {tenantId}</p>
          <CustomButton 
            variant="primary"
            size="medium"
            onClick={() => navigate('/organisations/create')}
          >
            Create New Organisation
          </CustomButton>
        </div>
      ) : (
        <Table data={organisations} />
      )}
    </Container>
  );
};

export default OrganisationsPage;
