import React from "react";
import { useNavigate } from "react-router-dom";
import { 
  Container, 
  Typography, 
  Box, 
  Paper,
  Button
} from '@mui/material';
import { styled } from '@mui/material/styles';

// Styled components using MUI's styled API
const StyledPaper = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(4),
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  gap: theme.spacing(3),
  maxWidth: 600,
  margin: '0 auto',
  marginTop: theme.spacing(4),
  backgroundColor: '#fff',
  boxShadow: '0 3px 5px 2px rgba(0, 0, 0, 0.1)',
}));

const WelcomeBox = styled(Box)(({ theme }) => ({
  textAlign: 'center',
  marginBottom: theme.spacing(4),
}));

function HomePage() {
  const navigate = useNavigate();
  const tenantId = 1; // Set default tenantId to 1

  const handleOrganisationsClick = () => {
    navigate('/organisations', { state: { tenantId } });
  };

  return (
    <Container>
      <StyledPaper elevation={3}>
        <WelcomeBox>
          <Typography variant="h3" component="h1" gutterBottom>
            Welcome to Requirements Tracker
          </Typography>
          <Typography variant="h6" color="textSecondary" gutterBottom>
            Your central hub for managing requirements and organizations
          </Typography>
        </WelcomeBox>
        
        <Typography variant="body1" paragraph>
          Select an option from the menu above to get started, or use the button below to view organizations.
        </Typography>

        <Button 
          variant="contained" 
          color="primary" 
          size="large"
          onClick={handleOrganisationsClick}
          sx={{
            minWidth: 200,
            fontWeight: 'bold',
            '&:hover': {
              backgroundColor: '#0F0D46', // Match your header color
            },
          }}
        >
          View Organisations
        </Button>
      </StyledPaper>
    </Container>
  );
}

export default HomePage;
